mapper.update:
update t_employee
set lastName = #{lastname,jdbcType=VARCHAR},
  email = #{email,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=INTEGER},
  d_id = #{dId,jdbcType=INTEGER}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, lastName, email, gender, d_id
from t_employee
where id = #{id,jdbcType=INTEGER}
